import React, { useState, useEffect } from 'react'
import { Link, useNavigate, useLocation, useParams } from 'react-router-dom'
import { Button, Row, Col, ListGroup, Image, Card } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import CheckoutSteps from '../components/CheckoutSteps'
import { getOrderDetails, payOrder } from '../actions/orderActions'
import { ORDER_CREATE_RESET } from '../constants/orderConstants'
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js";

function OrderScreen() {
    const orderId = useParams()['id']
  const dispatch = useDispatch()
  const navigate = useNavigate()

  const [sdkReady, setSdkReady] = useState(false)

  const orderDetails = useSelector(state => state.orderDetails)
  const { order, error, loading } = orderDetails

  const orderPay = useSelector(state => state.orderPay)
  const { loading:loadingPay, success:successPay} = orderPay

  if(!loading && !error){
      order.itemsPrice = order.orderItems.reduce((acc, item) => acc + item.price * item.qty, 0).toFixed(2)
      order.shippingPrice = (order.itemsPrice > 1000 ? 0 : 10).toFixed(2)
      order.totalPrice = (Number(order.itemsPrice) + Number(order.shippingPrice)).toFixed(2)

  }

  const addPayPalScript = () => {
    const script = document.createElement('script')
    script.type = 'text/javascript'
    script.source = 'https://www.paypal.com/sdk/js?client-id=AaktbTAzlrOV94yRBGCCpOBfM35VDvk8RC71xBjhKiP0YpgtwZCKcB4OiROu-sr6fSmARNPEvkjUDoRK&currency=EUR'
    script.async = true
    script.onload = () => {
        setSdkReady(true)
    }
    document.body.appendChild(script)
}


  // AaktbTAzlrOV94yRBGCCpOBfM35VDvk8RC71xBjhKiP0YpgtwZCKcB4OiROu-sr6fSmARNPEvkjUDoRK

//   if(!order.paymentMethod){
//     navigate('/payment')
//   }

  useEffect(() => {
    if(!order || successPay || order._id !== Number(orderId)){
      dispatch(getOrderDetails(orderId))
    } else if(!order.isPaid) {
        if(!window.paypal){
            addPayPalScript()
        } else {
            setSdkReady(true)
        }
    }
  }, [order, orderId, dispatch, successPay])

  const successPaymentHandler = (paymentResult) => {
    dispatch(payOrder(orderId, paymentResult))
  }

  return loading ? (
    <Loader /> 
  ) : error ? (
    <Message variant='danger'>{error}</Message>
  ) : (
        <div>
            <h1>Order: {order._id}</h1>
            <Row>
                <Col md={8}>
                <ListGroup variant='flush'>
                    <ListGroup.Item>
                    <h2>Shipping</h2>
                    
                    <p><strong>Name: </strong>{order.user.name}</p>
                    <p><strong>Email: </strong> <a href={`mailto:${order.user.email}`}>{order.user.email}</a></p>
                    
                    <p>
                        <strong>Shipping address: </strong>
                        {order.shippingAddress.address}, {order.shippingAddress.city},
                        {'           '}
                        {order.shippingAddress.postCode}, {order.shippingAddress.country}
                    </p>
                    {order.isDelivered ? (
                        <Message variant='success'>Delivered on {order.deliveredAt}</Message>
                    ) : (
                        <Message variant='warning'>Not delivered</Message>
                    )}
                    </ListGroup.Item>
                    <ListGroup.Item>
                    <h2>Payment method:</h2>
                    <p><strong>Method: </strong>{order.paymentMethod}</p>
                    {order.isPaid ? (
                        <Message variant='success'>Paid on {order.paidAt}</Message>
                    ) : (
                        <Message variant='warning'>Not paid</Message>
                    )}
                    </ListGroup.Item>
                    <ListGroup.Item>
                    <h2>Order Items :</h2>
                    {order.orderItems.length === 0 ? <Message variant='info'>Your order is empty</Message> :(
                        <ListGroup variant='flush'>
                        {order.orderItems.map((item, index) => (
                            <ListGroup.Item key={index}>
                            <Row>
                                <Col md={2} >
                                <Image src={item.image} alt={item.name} fluid rounded />
                                </Col>

                                <Col>
                                <Link to={`/product/${item.product}`}>{item.name}</Link>
                                </Col>

                                <Col md={4}>
                                {item.qty} X ${item.price} = ${(item.qty * item.price).toFixed(2)}
                                </Col>

                            </Row>
                            </ListGroup.Item>
                        ))}

                        </ListGroup>
                    )}
                    </ListGroup.Item>
                </ListGroup>
                </Col>
                
                <Col md={4}>
                <Card>
                    <ListGroup>
                        <ListGroup.Item>
                            <h2>Order summary</h2>
                        </ListGroup.Item>
                        
                        <ListGroup.Item>
                            <Row>
                            <Col>Item:</Col>
                            <Col>${order.itemsPrice}</Col>
                            </Row>
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <Row>
                            <Col>Shipping:</Col>
                            <Col>${order.shippingPrice}</Col>
                            </Row>
                        </ListGroup.Item>

                        <ListGroup.Item>
                            <Row>
                            <Col>Total price:</Col>
                            <Col>${order.totalPrice}</Col>
                            </Row>
                        </ListGroup.Item>

                        <ListGroup.Item>
                            {error && <Message variant='danger'>{error}</Message>}
                        </ListGroup.Item>
                        
                        {!order.isPaid && (
                            <ListGroup.Item>
                                {loadingPay && <Loader />}
                                {!sdkReady ? (
                                    <Loader />
                                ) : (
                                    <PayPalButtons 
                                    amount = {order.totalPrice}
                                    onSuccess={successPaymentHandler}/>
                                )}
                            </ListGroup.Item>

                        )}
                    

                    </ListGroup>
                </Card>
                </Col>
            </Row>
        </div>
  )
}

export default OrderScreen
