import React, { useState, useEffect } from 'react'
import { Link, useNavigate, useLocation, useParams, json } from 'react-router-dom'
import { Form, Row, Col, Button, check } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Loader from '../components/Loader'
import Message from '../components/Message'
import FormContainer from '../components/FormContainer'

import { listProductDetails, updateProduct } from '../actions/productActions'
import { PRODUCT_UPDATE_RESET } from '../constants/productConstants'

function ProductEditScreen() {

  const productId = useParams()['id']

  const [name, setName] = useState('')
  const [price, setPrice] = useState(0)
  const [image, setImage] = useState('')
  const [category, setCategory] = useState('')
  const [brand, setBrand] = useState('')
  const [countInStock, setCountInStock] = useState(0)
  const [description, setDescription] = useState(0)
  const [uploading, setUploading] = useState(false)


  const dispatch = useDispatch()
  const navigate = useNavigate()

  const productDetails = useSelector(state => state.productDetails)
  const { error, loading, product } = productDetails
  
  const productUpdate = useSelector(state => state.productUpdate)
  const { error: errorUpdate, loading: loadingUpdate, success: successUpdate } = productUpdate

  useEffect(() => {
    if (successUpdate) {
        // Reset productUpdate in the state
        dispatch({ type: PRODUCT_UPDATE_RESET })
        navigate('/admin/productlist')

    } else { 
        if (!product.name || product._id !== Number(productId)){
        dispatch(listProductDetails(productId))
        }
        else {
            setName(product.name)
            setPrice(product.price)
            setImage(product.image)
            setCategory(product.category)
            setBrand(product.brand)
            setCountInStock(product.countInStock)
            setDescription(product.description)
        }
    }
    
  }, [productId, product, dispatch, navigate, successUpdate])


  const submitHandler = (e) => {
    e.preventDefault()
    console.log('Update product')
    dispatch(updateProduct({
        _id: productId,
        name,
        price,
        image,
        brand,
        category,
        countInStock,
        description
    }))
  }


  const uploadFileHandler = async => {
    console.log('File upload')
  }
    return (
        <div>
            <Link to='/admin/productlist'>Go Back</Link>
            <FormContainer>
                <h1>Edit Product</h1>
                {loadingUpdate && <Loader/>}
                {errorUpdate && <Message variant='danger'>{errorUpdate}</Message>}

                {loading ? <Loader /> : error ? <Message variant='danger'>{error}</Message> 
                : (
                    <Form onSubmit={submitHandler}>
                    <Form.Group controlId='name' className='mb-3'>
                        <Form.Label>Name</Form.Label>
                        <Form.Control 
                            // required
                            type='name' 
                            placeholder='Enter name' 
                            value={name} 
                            onChange={(e) => setName(e.target.value)}>
                        </Form.Control>
                    </Form.Group>

                    <Form.Group controlId='price' className='mb-3'>
                        <Form.Label>Price</Form.Label>
                        <Form.Control 
                        // required
                        type='number' 
                        placeholder='Enter price' 
                        value={price} 
                        onChange={(e) => setPrice(e.target.value)}>
                        </Form.Control>
                    </Form.Group>
                    
                    {/* <Form.Group controlId='image'>
                                <Form.Label>Image</Form.Label>
                                <Form.Control

                                    type='text'
                                    placeholder='Enter image'
                                    value={image}
                                    onChange={(e) => setImage(e.target.value)}
                                >
                                </Form.Control>

                                <Form.File
                                    id='image-file'
                                    label='Choose File'
                                    custom
                                    onChange={uploadFileHandler}
                                >

                                </Form.File>
                                {/* {uploading && <Loader />}

                            </Form.Group> */}

                    <Form.Group controlId='image' className='mb-3'>
                        <Form.Label>Image</Form.Label>
                        <Form.Control 
                        // required
                        type='text' 
                        placeholder='Enter image' 
                        value={image} 
                        onChange={(e) => setImage(e.target.value)}>
                        </Form.Control>
                    </Form.Group>


                    <Form.Group controlId='category' className='mb-3'>
                        <Form.Label>category</Form.Label>
                        <Form.Control 
                        // required
                        type='text' 
                        placeholder='Enter category' 
                        value={category} 
                        onChange={(e) => setCategory(e.target.value)}>
                        </Form.Control>
                    </Form.Group>

                    <Form.Group controlId='brand' className='mb-3'>
                        <Form.Label>brand</Form.Label>
                        <Form.Control 
                        // required
                        type='text' 
                        placeholder='Enter brand' 
                        value={brand} 
                        onChange={(e) => setBrand(e.target.value)}>
                        </Form.Control>
                    </Form.Group>

                    <Form.Group controlId='countInStock' className='mb-3'>
                        <Form.Label>countInStock</Form.Label>
                        <Form.Control 
                        // required
                        type='number' 
                        placeholder='Enter stock' 
                        value={countInStock} 
                        onChange={(e) => setCountInStock(e.target.value)}>
                        </Form.Control>
                    </Form.Group>

                    <Form.Group controlId='description' className='mb-3'>
                        <Form.Label>description</Form.Label>
                        <Form.Control 
                        // required
                        type='text' 
                        placeholder='Enter description' 
                        value={description} 
                        onChange={(e) => setDescription(e.target.value)}>
                        </Form.Control>
                    </Form.Group>
            
                    <Button type='submit' variant='primary'>Update</Button>
                    </Form>
                )}
            </FormContainer>
        </div>
    )
}

export default ProductEditScreen
